import React, { useEffect, useState } from "react";
import "./CodeReview.css";
import "prismjs/themes/prism-tomorrow.css";
import Editor from "react-simple-code-editor";
import prism from "prismjs";

function Review() {
  const [code, setCode] = useState(`function sum(){
    return 1+1;
}`);

  useEffect(() => {
    prism.highlightAll();
  });

  return (
    <div className="container flex gap-[1rem] p-5 bg-zinc-900">
      <div className=" relative basis-2/4 flex flex-col bg-neutral-600 rounded-lg text-white">
        <div className="w-full h-full m-[-8px] mx-0">
          <Editor
            value={code}
            onChange={(code) => setCode(code)}
            highlight={(code) =>
              prism.highlight(code, prism.languages.javascript, "javascript")
            
            }
            padding={10}
            style={{
             fontWeight: 12,
             border: '1px solid #ddd',
             borderRadius: '5px',
             height: '100%'
            }}
          />
        </div>
        <div className="absolute bottom-3 right-5 text-white bg-blue-700 font-medium p-2 px-8 rounded-md cursor-pointer select-none">
          Review
        </div>
      </div>
      {/* bg-[#343434] */}
      <div className="basis-2/4 flex flex-col bg-teal-700 rounded-lg text-white p-2">
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Deleniti,
        culpa!
      </div>
    </div>
  );
}

export default Review;
