import React, { useEffect, useState } from "react";
import "./CodeReview.css";
import "prismjs/themes/prism-tomorrow.css";
import Editor from "react-simple-code-editor";
import prism from "prismjs";
import axios from "axios";
import Markdown from "react-markdown";
import rehypeHighlight from "rehype-highlight";
import "highlight.js/styles/github-dark.css";

function Review() {
  const [code, setCode] = useState(`function sum(){
    return 1+1;
}`);

  const [review, setReview] = useState(``);

  useEffect(() => {
    prism.highlightAll();
  });

  const reviewCode = async () => {
    try {
      const response = await axios.post("http://localhost:4000/ai/get-review", {
        code,
      });
      setReview(response.data);
    } catch (err) {
      console.error("Error fetching review:", err);
    }
  };

  return (
    <div className="container flex gap-[1rem] p-5 bg-zinc-900">
      {/* Left side editor */}
      <div className="relative basis-2/4 flex flex-col bg-neutral-700 rounded-lg text-white">
        <div className="w-full h-full">
          <Editor
            value={code}
            onValueChange={(code) => setCode(code)}
            highlight={(code) =>
              prism.highlight(code, prism.languages.javascript, "javascript")
            }
            padding={10}
            style={{
              fontSize: "1.1rem",
              fontFamily: '"Fira Code", "Fira Mono", monospace',
              fontWeight: 12,
              borderRadius: "5px",
              height: "100%",
              width: "100%",
            }}
          />
        </div>
        <div
          onClick={reviewCode}
          className="absolute bottom-3 right-5 text-white bg-blue-700 font-medium p-2 px-8 rounded-md cursor-pointer select-none"
        >
          Review
        </div>
      </div>

      {/* Right side AI Review */}
      {/* <div className="basis-2/4 flex flex-col bg-teal-800 rounded-lg text-white px-2 py-2 overflow-auto">
        <Markdown
          rehypePlugins={[rehypeHighlight]}
          style={{
            background: "#1e1e1e",
            padding: "10px",
            fontSize: "0.9rem",
            borderRadius: "6px",
            maxHeight: "300px",
            overflowX: "auto",
            overflowY: "auto",
          }}
        >
          {review}
        </Markdown>
      </div> */}
      <div className="basis-2/4 flex flex-col bg-teal-800 rounded-lg text-white px-2 py-2 review-panel">
        <Markdown
          key={review} // ðŸ”‘ force remount on new review, avoids removeChild error
          classame="markdown-body"
          rehypePlugins={[rehypeHighlight]}
        >
          {review}
        </Markdown>
      </div>
    </div>
  );
}

export default Review;
